css:
    table {
        width: 100%;
        border-collapse: collapse;
        margin-top: 20px;
    }
    th, td {
        border: 1px solid #ddd;
        padding: 8px;
        text-align: center;
        font-size: 12px;
    }
    th {
        background-color: #f2f2f2;
        color: #333;
        font-weight: bold;
    }
    tr:nth-child(even) {
        background-color: #f9f9f9;
    }
    tr:hover {
        background-color: #f1f1f1;
    }
    .header {
      text-align: center;

    }
    .page-header {
	border: none !important;
	width: 20%;
	position: fixed;
	top: 0;
    }
    @media screen {

		.page-footer,
		.page-footer-space {
			display: none;
		}

		.page-header,
		.page-header-space {
			display: none;
		}

	}

@media print {
	thead {
		display: table-header-group;
	}

	tfoot {
		display: table-footer-group;
	}

	.page-footer,
	.page-footer-space {
		height: 70px;
		margin-bottom: 0px;

	}

	.page-header,
	.page-header-space {
		height: 40px;
		margin-bottom: 50px;
		margin-top: 0px;
	}

}
.page-break {
        page-break-before: avoid;
    }

html:
{% set start_date = frappe.utils.getdate(doc.from_date) %}
{% set end_date = frappe.utils.getdate(doc.to_date) %}
{% set dates = [] %}

{% for i in range(frappe.utils.date_diff(end_date, start_date) + 1) %}
    {% set date = frappe.utils.add_days(start_date, i) %}
    {% set dates = dates.append(date) %}
{% endfor %}

<div class="header">
    <img src="/files/gsh new logo.latest.jpg" style="width: 100px; height: 50px; float: left;">
</div>
<br>
<div>
    <h4 style="text-align: center;">
        {{ doc.department or "" }} {{ doc.from_date }} to {{ doc.to_date }}
    </h4>
</div>
{%- if doc.department -%}
<table width="100%" style="width: 100%; border-collapse: collapse; text-align: center;">
    <thead>
        <tr>
            <th style="text-align: center; width:20%;" width="20%">Employee</th>
            {% for date in dates %}
                <th style="text-align: center; width: 10%;" width="10%">{{ date.strftime('%A (%d/%m)') }}</th>
            {% endfor %}
        </tr>
    </thead>
    <tbody>
        {% for employee in frappe.get_all('Employee', filters={'department': doc.department, 'status': 'Active'}, fields=['name', 'employee_name']) %}
        <tr>
            <td style="text-align: center;">{{ employee.employee_name }}</td>
            {% for date in dates %}
            <td style="text-align: center;">
                {% set shift = frappe.get_all('Shift Assignment',
                    filters={
                        'employee': employee.name,
                        'start_date': date,
                        'docstatus': 1,
                        'department': doc.department
                    },
                    fields=[ 'shift_type', 'custom_location_name' ],
                    limit=1
                ) %}
                {% if shift %}
                    {{ shift[0].shift_type }}<br>
                    <i> {{ shift[0].custom_location_name or "" }} </i>
                {% else %}

                {% endif %}
            </td>
            {% endfor %}
        </tr>
        {% endfor %}
    </tbody>
</table>
{%- else -%}
<table width="100%" style="width: 100%; border-collapse: collapse; text-align: center;">
    <thead>
        <tr>
            <th style="text-align: center; width:20%;" width="20%">Employee</th>
            {% for date in dates %}
                <th style="text-align: center; width: 10%;" width="10%">{{ date.strftime('%A (%d/%m)') }}</th>
            {% endfor %}
        </tr>
    </thead>
    <tbody>
        {% for employee in frappe.get_all('Employee', filters={'status': 'Active'}, fields=['name', 'employee_name']) %}
        <tr>
            <td style="text-align: center;">{{ employee.employee_name }}</td>
            {% for date in dates %}
            <td style="text-align: center;">
                {% set shift = frappe.get_all('Shift Assignment',
                    filters={
                        'employee': employee.name,
                        'start_date': date,
                        'docstatus': 1
                    },
                    fields=[ 'shift_type', 'custom_location_name' ],
                    limit=1
                ) %}
                {% if shift %}
                    {{ shift[0].shift_type }}<br>
                    <i> {{ shift[0].custom_location_name or "" }} </i>
                {% else %}

                {% endif %}
            </td>
            {% endfor %}
        </tr>
        {% endfor %}
    </tbody>
</table>
{%- endif -%}
